<span class="line">    <span class="token keyword">for</span> result <span class="token keyword">in</span> results <span class="token punctuation">{</span></span>
<span class="line">        <span class="token keyword">match</span> result <span class="token punctuation">{</span></span>
<span class="line">            <span class="token class-name">Ok</span><span class="token punctuation">(</span>data<span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token macro property">println!</span><span class="token punctuation">(</span><span class="token string">"成功: {}"</span><span class="token punctuation">,</span> data<span class="token punctuation">)</span><span class="token punctuation">,</span></span>
