<span class="line">    <span class="token keyword">fn</span> <span class="token function-definition function">from</span><span class="token punctuation">(</span>error<span class="token punctuation">:</span> <span class="token namespace">io<span class="token punctuation">::</span></span><span class="token class-name">Error</span><span class="token punctuation">)</span> <span class="token punctuation">-></span> <span class="token keyword">Self</span> <span class="token punctuation">{</span></span>
<span class="line">        <span class="token class-name">FileError</span><span class="token punctuation">::</span><span class="token class-name">IoError</span><span class="token punctuation">(</span>error<span class="token punctuation">)</span></span>
