<span class="line">        <span class="token class-name">Err</span><span class="token punctuation">(</span><span class="token class-name">FileError</span><span class="token punctuation">::</span><span class="token class-name">EmptyFile</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token macro property">println!</span><span class="token punctuation">(</span><span class="token string">"文件为空"</span><span class="token punctuation">)</span><span class="token punctuation">,</span></span>
<span class="line">        <span class="token class-name">Err</span><span class="token punctuation">(</span><span class="token class-name">FileError</span><span class="token punctuation">::</span><span class="token class-name">InvalidContent</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token macro property">println!</span><span class="token punctuation">(</span><span class="token string">"文件包含无效内容"</span><span class="token punctuation">)</span><span class="token punctuation">,</span></span>
<span class="line">        <span class="token class-name">Err</span><span class="token punctuation">(</span><span class="token class-name">FileError</span><span class="token punctuation">::</span><span class="token class-name">IoError</span><span class="token punctuation">(</span>e<span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token macro property">println!</span><span class="token punctuation">(</span><span class="token string">"IO错误: {}"</span><span class="token punctuation">,</span> e<span class="token punctuation">)</span><span class="token punctuation">,</span></span>
